#!/bin/bash

set -o nounset
set -o errexit
set -o pipefail

#check versions
oc version || true
openshift-install version || true
which openshift-install || true

#Check AWS CLI
AWS_ACCESS_KEY_ID=$(cat /var/run/quay-qe-omr-secret/access_key) && export AWS_ACCESS_KEY_ID
AWS_SECRET_ACCESS_KEY=$(cat /var/run/quay-qe-omr-secret/secret_key) && export AWS_SECRET_ACCESS_KEY
AWS_DEFAULT_REGION="us-west-2" && export AWS_DEFAULT_REGION
aws s3 ls

cat "${SHARED_DIR}/new_pull_secret" | jq
cat "${SHARED_DIR}/local_registry_icsp_file.yaml"
OMR_HOST_NAME=$(cat ${SHARED_DIR}/OMR_HOST_NAME)
OCP_NAME="omrocpprowci$RANDOM"

echo ${OMR_HOST_NAME}
echo ${OCP_NAME}

cat >> ${SHARED_DIR}/install-config.yaml << EOF
apiVersion: v1
baseDomain: qe.devcluster.openshift.com 
credentialsMode: Mint
controlPlane:   
  hyperthreading: Enabled 
  name: master
  platform:
    aws:
      zones:
      - us-west-2a
      - us-west-2b
      rootVolume:
        iops: 4000
        size: 500
        type: io1 
      type: m4.2xlarge
  replicas: 
compute: 
- hyperthreading: Enabled 
  name: worker
  platform:
    aws:
      rootVolume:
        iops: 2000
        size: 500
        type: io1 
      type: m4.2xlarge
      zones:
      - us-west-2c
  replicas: 3
metadata:
  name: "${OCP_NAME}"
networking:
  clusterNetwork:
  - cidr: 10.128.0.0/14
    hostPrefix: 23
  machineNetwork:
  - cidr: 10.0.0.0/16
  networkType: OpenShiftSDN
  serviceNetwork:
  - 172.30.0.0/16
platform:
  aws:
    region: us-west-2
    userTags:
      adminContact: luffy
      costCenter: 7536
fips: false 
sshKey: ssh-rsa  lizhang@lzha-mac 
pullSecret: '{"auths":{"${OMR_HOST_NAME}:8443": {"auth": "cXVheTpwYXNzd29yZA==","email": "lzha@redhat.com"}, "registry.ci.openshift.org": {"auth": "","email": "lzha@redhat.com"}}}'
additionalTrustBundle: |
$(cat "${SHARED_DIR}/rootCA.pem" | awk '{print "    "$0}')
$(cat "${SHARED_DIR}/local_registry_icsp_file.yaml")
EOF

cp ${SHARED_DIR}/install-config.yaml /tmp
openshift-install --dir=/tmp create cluster --log-level=debug || true



